package Example2;

public class Datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte b = 12;
		short s = 20;
		int i = 30;
		long l = 125l;
		double d = 2987d;
		float f = 234.4f;
		char ch = 'A';
		boolean bl = true;
		System.out.println("Primitive datatypes are:" +b + " " +s + " "+i +" "+l + " "+d +" "+f +" "+ch +" "+bl);

	}

}
